import type { $ZodStringFormats } from "../core/checks.js";
import type * as errors from "../core/errors.js";
import * as util from "../core/util.js";

const error: () => errors.$ZodErrorMap = () => {
  const Sizable: Record<string, { unit: string; verb: string }> = {
    string: { unit: "carÃ cters", verb: "contenir" },
    file: { unit: "bytes", verb: "contenir" },
    array: { unit: "elements", verb: "contenir" },
    set: { unit: "elements", verb: "contenir" },
  };

  function getSizing(origin: string): { unit: string; verb: string } | null {
    return Sizable[origin] ?? null;
  }

  const parsedType = (data: any): string => {
    const t = typeof data;

    switch (t) {
      case "number": {
        return Number.isNaN(data) ? "NaN" : "number";
      }
      case "object": {
        if (Array.isArray(data)) {
          return "array";
        }
        if (data === null) {
          return "null";
        }

        if (Object.getPrototypeOf(data) !== Object.prototype && data.constructor) {
          return data.constructor.name;
        }
      }
    }
    return t;
  };

  const Nouns: {
    [k in $ZodStringFormats | (string & {})]?: string;
  } = {
    regex: "entrada",
    email: "adreÃ§a electrÃ²nica",
    url: "URL",
    emoji: "emoji",
    uuid: "UUID",
    uuidv4: "UUIDv4",
    uuidv6: "UUIDv6",
    nanoid: "nanoid",
    guid: "GUID",
    cuid: "cuid",
    cuid2: "cuid2",
    ulid: "ULID",
    xid: "XID",
    ksuid: "KSUID",
    datetime: "data i hora ISO",
    date: "data ISO",
    time: "hora ISO",
    duration: "durada ISO",
    ipv4: "adreÃ§a IPv4",
    ipv6: "adreÃ§a IPv6",
    cidrv4: "rang IPv4",
    cidrv6: "rang IPv6",
    base64: "cadena codificada en base64",
    base64url: "cadena codificada en base64url",
    json_string: "cadena JSON",
    e164: "nÃºmero E.164",
    jwt: "JWT",
    template_literal: "entrada",
  };

  return (issue) => {
    switch (issue.code) {
      case "invalid_type":
        return `Tipus invÃ lid: s'esperava ${issue.expected}, s'ha rebut ${parsedType(issue.input)}`;
      // return `Tipus invÃ lid: s'esperava ${issue.expected}, s'ha rebut ${util.getParsedType(issue.input)}`;
      case "invalid_value":
        if (issue.values.length === 1) return `Valor invÃ lid: s'esperava ${util.stringifyPrimitive(issue.values[0])}`;
        return `OpciÃ³ invÃ lida: s'esperava una de ${util.joinValues(issue.values, " o ")}`;
      case "too_big": {
        const adj = issue.inclusive ? "com a mÃ xim" : "menys de";
        const sizing = getSizing(issue.origin);
        if (sizing)
          return `Massa gran: s'esperava que ${issue.origin ?? "el valor"} continguÃ©s ${adj} ${issue.maximum.toString()} ${sizing.unit ?? "elements"}`;
        return `Massa gran: s'esperava que ${issue.origin ?? "el valor"} fos ${adj} ${issue.maximum.toString()}`;
      }
      case "too_small": {
        const adj = issue.inclusive ? "com a mÃ­nim" : "mÃ©s de";
        const sizing = getSizing(issue.origin);
        if (sizing) {
          return `Massa petit: s'esperava que ${issue.origin} continguÃ©s ${adj} ${issue.minimum.toString()} ${sizing.unit}`;
        }

        return `Massa petit: s'esperava que ${issue.origin} fos ${adj} ${issue.minimum.toString()}`;
      }
      case "invalid_format": {
        const _issue = issue as errors.$ZodStringFormatIssues;
        if (_issue.format === "starts_with") {
          return `Format invÃ lid: ha de comenÃ§ar amb "${_issue.prefix}"`;
        }
        if (_issue.format === "ends_with") return `Format invÃ lid: ha d'acabar amb "${_issue.suffix}"`;
        if (_issue.format === "includes") return `Format invÃ lid: ha d'incloure "${_issue.includes}"`;
        if (_issue.format === "regex") return `Format invÃ lid: ha de coincidir amb el patrÃ³ ${_issue.pattern}`;
        return `Format invÃ lid per a ${Nouns[_issue.format] ?? issue.format}`;
      }
      case "not_multiple_of":
        return `NÃºmero invÃ lid: ha de ser mÃºltiple de ${issue.divisor}`;
      case "unrecognized_keys":
        return `Clau${issue.keys.length > 1 ? "s" : ""} no reconeguda${issue.keys.length > 1 ? "s" : ""}: ${util.joinValues(issue.keys, ", ")}`;
      case "invalid_key":
        return `Clau invÃ lida a ${issue.origin}`;
      case "invalid_union":
        return "Entrada invÃ lida"; // Could also be "Tipus d'uniÃ³ invÃ lid" but "Entrada invÃ lida" is more general
      case "invalid_element":
        return `Element invÃ lid a ${issue.origin}`;
      default:
        return `Entrada invÃ lida`;
    }
  };
};

export default function (): { localeError: errors.$ZodErrorMap } {
  return {
    localeError: error(),
  };
}





